import { useEffect, useState } from 'react';
import './App.css';
import Movies from './local-json/movies-filter-react.json'
import MovieCard from './components/MovieCard';
import NavbarUI from './components/NavbarUI';

function App() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch('./local-json/movies-filter-react.json'); // Replace with your API endpoint
      if (!response.ok) {
        throw new Error('Network response was not ok.');
      }
      const data = await response.json();
      setData(data);
      setLoading(false);
    } catch (error) {
      setError(error.message);
      setLoading(false);
    }
  };

  return (
    // <div class="bg-gradient-to-bl from-blue-50 to-violet-50 flex items-center justify-center ">
    //   <div class="container  mx-auto p-4">
    //     <div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-2 lg:grid-cols-4 xl:grid-cols-4 gap-4">
    //       <NavbarUI />
    //       {/* {loading ? (
    //     <p>Loading...</p>
    //   ) : error ? (
    //     <p>Error: {error}</p>
    //   ) : (
    //     <ul>
    //       {data.map((item) => (
    //         <li key={item.id}>hi</li>
    //         // <UserUI data={item} key={item.id} />
    //       ))}
    //     </ul>
    //   )} */}

    //       {Movies.map((item) => (
    //         // <li key={item.id}>{item.movietitle}</li>
    //         // <UserUI data={item} key={item.id} />
    //         <MovieCard data={item} key={item.id} />
    //       ))}
    //     </div>
    //   </div>
    // </div>


<div class="bg-[#ffd148] flex items-center justify-center min-h-screen relative z-10">
    <div class="absolute -top-40 -right-40 lg:w-2/3 w-full h-80 -z-10">
        <svg xmlns='http://www.w3.org/2000/svg' class="drop-shadow-[-20px_10px_0px_#ffc949]" viewBox='0 0 2000 2000'
            fill='#ffb14a'>
            <path
                d='M994 112c-703-2-920.47 400.35-904 905 13.35 409 32.03 946.66 977 861 684-62 792-279 835-777 61.67-714.25-288.33-987.24-908-989Z'>
            </path>
        </svg>
    </div>

    <div
        class="w-full max-w-[1190px] px-6 sm:px-8 md:px-16 py-10 md:py-20 rounded-xl bg-[#ffffff]/60 min-h-[300px] m-2 shadow-[0px_14px_28px_-5px_rgba(0,0,0,0.21)]">
        <h1 class="font-semibold text-2xl mb-2 tracking-wider drop-shadow-[3px_3px_5px_rgba(91,91,91,0.58)]">Website -
            Sponsorship</h1>
        <small class="font-[500]">In the event that we do not have a full 30-days, we extrapolate based on data we have.</small>
        <ul class="flex items-start justify-between gap-8 mt-10 md:flex-row flex-col">
            <li
                class="w-full lg:w-[32%] px-10 pt-16 pb-8 bg-white rounded-lg self-stretch hover:scale-105 hover:shadow-[0px_14px_28px_-5px_rgba(0,0,0,0.1)] transition-all duration-150 ease-in">
                <h2
                    class="text-center font-semibold text-lg tracking-wider mb-3 drop-shadow-[3px_3px_5px_rgba(91,91,91,0.58)]">
                    Sidebar Block</h2>
                <p class="text-center tracking-tighter block mb-14">
                    <span class="text-4xl font-bold">$250</span>
                    <span class="text-black/40 text-center">/mo</span>
                </p>
                <button class="w-full p-2 bg-[#13181f] text-white rounded-md font-semibold hover:bg-[#ff2865] transition-all duration-150 ease-in mb-8 border-[#13181f]">add to cart</button>
                <ol class="list-disc text-[#ff2865] w-[80%] mx-auto">
                    <li><span class="text-black text-xs font-semibold">439,000 Est. Impressions</span></li>
                    <li><span class="text-black text-xs font-semibold">180 x 150</span></li>
                    <li><span class="text-black text-xs font-semibold">Middle Right</span></li>
                </ol>
            </li>
            <li
                class="w-full lg:w-[32%] px-10 pt-16 pb-8 bg-white rounded-lg self-stretch hover:scale-105 hover:shadow-[0px_14px_28px_-5px_rgba(0,0,0,0.1)] transition-all duration-150 ease-in relative group">
                <div
                    class="absolute top-0 left-1/2 -translate-x-1/2 rounded-br-xl rounded-bl-xl text-white px-8 py-2 bg-[#ff2865] transition-all duration-150 ease-in group-hover:shadow-[0px_14px_28px_-5px_rgba(0,0,0,0.1)]">
                    Hot take!
                </div>
                <h2
                    class="text-center font-semibold text-lg tracking-wider mb-3 drop-shadow-[3px_3px_5px_rgba(91,91,91,0.58)]">
                    Sidebar Block</h2>
                <p class="text-center tracking-tighter block mb-14">
                    <span class="text-4xl font-bold">$400</span>
                    <span class="text-black/40 text-center">/mo</span>
                </p>
                <button class="w-full p-2 bg-[#13181f] text-white rounded-md font-semibold hover:bg-[#ff2865] transition-all duration-150 ease-in mb-8 border-[#13181f]">add to cart</button>
                <ol class="list-disc text-[#ff2865] w-[80%] mx-auto">
                    <li><span class="text-black text-xs font-semibold">439,000 Est. Impressions</span></li>
                    <li><span class="text-black text-xs font-semibold">300 x 250</span></li>
                    <li><span class="text-black text-xs font-semibold">Middle Right</span></li>
                </ol>
            </li>
            <li
                class="w-full lg:w-[32%] px-10 pt-16 pb-8 bg-white rounded-lg self-stretch hover:scale-105 hover:shadow-[0px_14px_28px_-5px_rgba(0,0,0,0.1)] transition-all duration-150 ease-in relative group">
                <div
                    class="absolute top-0 left-1/2 -translate-x-1/2 rounded-br-xl rounded-bl-xl bg-[#e2e2e2] text-black px-8 py-2 group-hover:bg-red-800 group-hover:text-white transition-all duration-150 ease-in group-hover:shadow-[0px_14px_28px_-5px_rgba(0,0,0,0.1)]">
                    Sold Out
                </div>
                <h2
                    class="text-center font-semibold text-lg tracking-wider mb-3 drop-shadow-[3px_3px_5px_rgba(91,91,91,0.58)]">
                    Premium Sidebar</h2>
                <p class="text-center tracking-tighter block mb-14">
                    <span class="text-4xl font-bold">$2,600</span>
                    <span class="text-black/40 text-center">/mo</span>
                </p>
                <button class="w-full p-2 bg-transparent text-[#13181f] rounded-md font-semibold hover:bg-[#13181f] hover:text-white transition-all duration-150 ease-in mb-8 border border-[#13181f]">get on the waiting list</button>
                <ol class="list-disc text-[#ff2865] w-[80%] mx-auto">
                    <li><span class="text-black text-xs font-semibold">2,000,000 Est. Impressions</span></li>
                    <li><span class="text-black text-xs font-semibold">300 x 250</span></li>
                    <li><span class="text-black text-xs font-semibold">Top Right</span></li>
                </ol>
            </li>
        </ul>
    </div>
</div>

  );
}

export default App;
